import { useState } from "react";
import "./app.css";
import FormInput from "./components/FormInput";
import { Country, State, City } from 'country-state-city';
import "./components/formInput.css"

const App = () => {
  var countries = Country.getAllCountries()
  var states = State.getAllStates()
  var cities = City.getAllCities()

  // console.log(Country.getAllCountries()


  console.log(State.getAllStates())
  // console.log(City.getAllCities())
  const [desh, setDesh] = useState([])

  const [values, setValues] = useState({
    uame: "",
    email: "",
    mobileno: "",
    password: "",
    confirmPassword: "",
  });

  const inputs = [
    {
      id: 1,
      name: "name",
      type: "text",
      placeholder: "Name",
      errorMessage:
        "Name should be 3-16 characters and shouldn't include any special character!",
      label: "Name",
      pattern: "^[A-Za-z0-9]{3,16}$",
      required: true,
    },
    {
      id: 2,
      name: "email",
      type: "email",
      placeholder: "Email",
      errorMessage: "It should be a valid email address!",
      label: "Email",
      required: true,
    },
    {
      id: 3,
      name: "mobileno",
      type: "tel",
      placeholder: "Mobile No.",
      label: "Mobile No",
    },
    {
      id: 4,
      name: "password",
      type: "password",
      placeholder: "Password",
      errorMessage:
        "Password should be 8-20 characters and include at least 1 letter, 1 number and 1 special character!",
      label: "Password",
      pattern: `^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,20}$`,
      required: true,
    },
    {
      id: 5,
      name: "confirmPassword",
      type: "password",
      placeholder: "Confirm Password",
      errorMessage: "Passwords don't match!",
      label: "Confirm Password",
      pattern: values.password,
      required: true,
    },
    // {
    //   id: 6,
    //   name: "country",
    //   type: "text",
    //   placeholder: "Country",
    //   label: "Country",
    // },
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted");
  };

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };
  const onCountryChange = (e) => {
    console.log(1)
    setDesh({ ...desh, [e.target.name]: e.target.value });
    console.log({ ...desh, [e.target.name]: e.target.value });
  };

  return (
    <div className="app">
      <form onSubmit={handleSubmit}>
        <h1>Register</h1>
        {inputs.map((input) => (
          <FormInput
            key={input.id}
            {...input}
            value={values[input.name]}
            onChange={onChange}
          />
        ))}
        <label>Country</label>
        <input
          className="formInput"
          list="datalistCountry"
          label="Country"
          name="country"
          id="exampleDataList"
          placeholder="Type to search country..."
          onChange={onCountryChange}

        />
        <datalist id="datalistCountry">
          {countries.map((country) => (
            <option
              key={country.name}
              value={country.name}
            />
          ))}
        </datalist>
        <label>State</label>
        <input
          className="formInput"
          list="datalistState"
          label="State"
          name="state"
          id="exampleDataList1"
          placeholder="Type to search state..."

        />
        <datalist id="datalistState">
          {states.map((state, index) => (
            <option
              key={index}
              value={state.name}
              onChange={onChange}
            />
          ))}

        </datalist>
        {/* <label>City</label>
        <input
          className="formInput"
          list="datalistCity"
          label="City"
          name="city"
          id="exampleDataList2"
          placeholder="Type to search city..."

        />
        <datalist id="datalistCity">
          {cities.map((city) => (
            <option
              key={city.stateCode}
              value={city.name}
              onChange={onChange}
            />
          ))}

        </datalist> */}
        <button>Submit</button>
      </form>
    </div>
  );
};

export default App;
